mapper.insert:
INSERT INTO BOOK_BNR (
    USER_CODE
    , BOOK_CODE
    , BORROW_DATE
    , EX_RETURN_DATE
) VALUES (
    #{userCode}
    , #{bookCode}
    , #{borrowDate}
    DATE_ADD(NOW(), INTERVAL +14 DAY)
)

mapper.select:
SELECT USER_CODE
FROM USERS
WHERE CARD_NUM = #{cardNum}

SELECT BOOK_BORROW_AVAILABLE
FROM BOOK_INFO
WHERE BOOK_CODE = #{bookCode}
