mapper.insert:
INSERT INTO BOARD(
    BOARD_TITLE
    , CONTENT
    , BOARD_DATE
    , BOARD_TYPE
    , USER_CODE
    , BOARD_CNT
    , BOARD_NO
)
VALUES(
    #{boardTitle}
    ,#{content}
    ,#{boardDate}
    ,#{boardType}
    ,#{userCode}
    ,#{boardCnt}
    ,#{boardNo}
)

mapper.select:
SELECT BOARD.BOARD_NO , BOARD_TITLE , CONTENT , BOARD_DATE , BOARD.USER_CODE , BOARD_CNT , USER_ID , IMG_CODE , ORIGIN_FILE_NAME , ATTACHED_FILE_NAME , IS_MAIN , BOARD_TYPE FROM board INNER JOIN USERS ON BOARD.USER_CODE = USERS.USER_CODE INNER JOIN IMAGE ON BOARD.BOARD_NO = IMAGE.BOARD_NO WHERE BOARD_TYPE = ${boardType}
where ${searchType} LIKE CONCAT('%', #{searchValue} , '%')
AND DATE_FORMAT(BOARD_DATE, '%Y-%m-%d') >= #{fromDate}
AND DATE_FORMAT(BOARD_DATE, '%Y-%m-%d') <= #{toDate}
ORDER BY BOARD_NO DESC LIMIT ${displayDataCnt} OFFSET ${displayDataCnt * (nowPage - 1)}

SELECT BOARD.BOARD_NO , BOARD_TITLE , CONTENT , BOARD_DATE , BOARD.USER_CODE , BOARD_CNT , USER_ID , USER_NAME FROM board INNER JOIN USERS ON BOARD.USER_CODE = USERS.USER_CODE
where ${searchType} LIKE CONCAT('%', #{searchValue} , '%')
ORDER BY BOARD_NO DESC LIMIT ${displayDataCnt} OFFSET ${displayDataCnt * (nowPage - 1)}

