mapper.insert:
INSERT INTO BOARD(
BOARD_TITLE
, CONTENT
, BOARD_DATE
, BOARD_TYPE
, USER_CODE
, BOARD_CNT
, BOARD_NO
)
VALUES(
#{boardTitle}
,#{content}
,#{boardDate}
,#{boardType}
,#{userCode}
,#{boardCnt}
,#{boardNo}
)

mapper.select:
SELECT BOARD.BOARD_NO , BOARD_TITLE , CONTENT , DATE_FORMAT(BOARD_DATE, '%Y-%m-%d') AS BOARD_DATE , BOARD.USER_CODE , BOARD_CNT , USER_NAME , ATTACHED_CODE , ORIGIN_FILE_NAME , ATTACHED_FILE_NAME , IS_MAIN , BOARD_TYPE FROM board INNER JOIN USERS ON BOARD.USER_CODE = USERS.USER_CODE INNER JOIN ATTACHED_FILE AS FILE ON BOARD.BOARD_NO = FILE.BOARD_NO WHERE BOARD_TYPE = ${boardType}
AND ${searchType} LIKE CONCAT('%', #{searchValue} , '%')
AND DATE_FORMAT(BOARD_DATE, '%Y-%m-%d') = #{fromDate}
AND DATE_FORMAT(BOARD_DATE, '%Y-%m-%d') = #{toDate}
ORDER BY BOARD_NO DESC LIMIT ${displayDataCnt} OFFSET ${displayDataCnt * (nowPage - 1)}

mapper.delete:
DELETE FROM BOARD
WHERE BOARD_NO IN

    #{boardNo}

mapper.update:
UPDATE BOARD
SET
BOARD_TITLE = #{boardTitle}
,CONTENT = #{content}
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT BOARD_TITLE ,TEACHER ,TARGET ,OPEN_DATE ,CLOSE_DATE ,TO_DATE ,FROM_DATE ,PERSONNEL ,MAXIMUM_PERSON ,EVENT_STATUS ,B.BOARD_NO FROM BOARD B INNER JOIN BOARD_PLUS P ON B.BOARD_NO = P.BOARD_NO WHERE BOARD_TYPE = ${boardType}
AND ${searchType} LIKE CONCAT('%', #{searchValue} , '%')
AND DATE_FORMAT(BOARD_DATE, '%Y-%m-%d') = #{fromDate}
AND DATE_FORMAT(BOARD_DATE, '%Y-%m-%d') = #{toDate}
ORDER BY BOARD_NO DESC LIMIT ${displayDataCnt} OFFSET ${displayDataCnt * (nowPage - 1)}
