mapper.select:
select *
from find_book_view
where 1 = 1

    AND ${searchType} LIKE CONCAT('%',#{searchValue},'%')


    and find_book_view.book_borrow_available = #{bookBorrowAvailable}


    and book_cate_name = #{bookCateName}


    and book_mid_cate_name = #{bookMidCateName}

LIMIT ${displayDataCnt} offset ${(displayDataCnt) * (nowPage - 1)}

select count(*)
from find_book_view
where
1 = 1

    and ${searchType} LIKE CONCAT('%',#{searchValue},'%')


    and find_book_view.book_borrow_available = #{bookBorrowAvailable}


    and book_cate_name = #{bookCateName}


    and book_mid_cate_name = #{bookMidCateName}

SELECT find_book_view.BOOK_CODE, BOOK_TITLE, BOOK_WRITER, BOOK_PUB, BOOK_YEAR, book_info.BOOK_BORROW_AVAILABLE, book_info.book_intro, book_info.BOOK_INFO_ATTACHED_FILE_NAME, BOOK_CATE_NAME, BOOK_MID_CATE_NAME, book_info.BOOK_REGDATE FROM find_book_view INNER JOIN book_info ON find_book_view.BOOK_CODE = book_info.BOOK_CODE where 1 = 1
and DATE_FORMAT(find_book_view.BOOK_REGDATE, '%Y-%m-%d') >= #{searchDate}
and find_book_view.book_borrow_available = #{bookBorrowAvailable}
and book_cate_name = #{bookCateName}
and book_mid_cate_name = #{bookMidCateName}
ORDER by book_info.BOOK_REGDATE desc LIMIT ${displayDataCnt} offset ${(displayDataCnt) * (nowPage - 1)}

select count(*)
from find_book_view
where
1 = 1

    and DATE_FORMAT(find_book_view.BOOK_REGDATE, '%Y-%m-%d') >= #{searchDate}


    and find_book_view.book_borrow_available = #{bookBorrowAvailable}


    and book_cate_name = #{bookCateName}


    and book_mid_cate_name = #{bookMidCateName}

select * from find_book_view where 1 = 1
and USER_TYPE = #{userType}
AND USER_TYPE IS not NULL AND USER_TYPE != ''

mapper.update:
UPDATE book_info
SET BOOK_REGDATE = DATE_SUB(BOOK_REGDATE, INTERVAL #{bookInfoNum} DAY)
WHERE book_info_num = #{bookInfoNum}
