mapper.insert:
INSERT INTO BOARD (
    BOARD_TITLE
    , CONTENT
    , BOARD_DATE
    , BOARD_TYPE
    , USER_CODE
    , BOARD_CNT
) VALUES (
    #{boardTitle}
    , #{content}
    , #{boardDate}
    , #{boardType}
    , #{userCode}
    , #{boardCnt}
)

mapper.update:
UPDATE BOARD
SET BOARD_CNT = BOARD_CNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT BOARD.BOARD_NO , BOARD_TITLE , CONTENT , BOARD_DATE , BOARD.USER_CODE , BOARD_CNT , USER_ID , USER_NAME FROM board INNER JOIN USERS ON BOARD.USER_CODE = USERS.USER_CODE where BOARD_TYPE = #{boardType}
and ${searchType} LIKE CONCAT('%', #{searchValue} , '%')
ORDER BY BOARD_NO DESC LIMIT ${displayDataCnt} OFFSET ${displayDataCnt * (nowPage - 1)}
