)
VALUES(
#{boardTitle}
,#{content}
,#{boardType}
,#{userCode}
,#{boardCnt}
,#{boardNum}
)

mapper.select:
SELECT BOARD.BOARD_NUM , BOARD.BOARD_TITLE , DATE_FORMAT(BOARD.BOARD_DATE, '%Y-%m-%d') AS BOARD_DATE , USERS.USER_NAME , USERS.USER_CODE , BOARD.BOARD_CNT , ASK_AND_ANSWER_BOARD.ASK_AND_ANSWER_BOARD_NUM , ASK_AND_ANSWER_BOARD.ASK_AND_ANSWER_BOARD_PUBLIC_OR_PRIVATE , ASK_AND_ANSWER_BOARD.ASK_AND_ANSWER_BOARD_PASSWORD , ASK_AND_ANSWER_BOARD.CHK_ASK_USER_CODE , ASK_AND_ANSWER_BOARD.IS_ANSWER_BOARD , ASK_AND_ANSWER_BOARD.IF_ANSWER_BOARD_NUM , ask_and_answer_board.ORIGIN_ORDER_NUM , ask_and_answer_board.ANSWER_ORDER_NUM FROM BOARD INNER JOIN users ON board.USER_CODE = users.USER_CODE INNER JOIN ASK_AND_ANSWER_BOARD ON board.BOARD_NUM = ASK_AND_ANSWER_BOARD.BOARD_NUM WHERE board_type = 30
and ${searchType} LIKE CONCAT('%', #{searchValue} , '%')
ORDER BY ask_and_answer_board.ORIGIN_ORDER_NUM DESC , ask_and_answer_board.ANSWER_ORDER_NUM ASC LIMIT ${displayDataCnt} OFFSET ${displayDataCnt * (nowPage - 1)}

mapper.update:
UPDATE BOARD
SET BOARD_CNT = BOARD_CNT + 1
WHERE BOARD_NUM = ${boardNum}

mapper.select:
SELECT BOARD.BOARD_NUM , BOARD.BOARD_TITLE , DATE_FORMAT(BOARD.BOARD_DATE, '%Y-%m-%d') AS BOARD_DATE , USERS.USER_NAME , BOARD.BOARD_CNT , ASK_AND_ANSWER_BOARD.ASK_AND_ANSWER_BOARD_NUM , ASK_AND_ANSWER_BOARD.ASK_AND_ANSWER_BOARD_PUBLIC_OR_PRIVATE , ASK_AND_ANSWER_BOARD.IS_ANSWER_BOARD , ASK_AND_ANSWER_BOARD.IF_ANSWER_BOARD_NUM FROM BOARD INNER JOIN users ON board.USER_CODE = users.USER_CODE INNER JOIN ASK_AND_ANSWER_BOARD ON board.BOARD_NUM = ASK_AND_ANSWER_BOARD.BOARD_NUM WHERE board_type = 30 AND is_answer_board = 'Y'
and ${searchType} LIKE CONCAT('%', #{searchValue} , '%')
ORDER BY BOARD_NUM DESC LIMIT ${displayDataCnt} OFFSET ${displayDataCnt * (nowPage - 1)}

